import React from 'react';
import { withRouter } from 'react-router-dom';
import { Alert } from 'react-bootstrap'

class PaymentPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cardNumber: '',
      cardExpires: '',
      cardSecureNumber: '',
    };

    this.handleCardNameChange = this.handleCardNameChange.bind(this);
    this.handleCardExpireChange = this.handleCardExpireChange.bind(this);
    this.handleCardSecureNumberChange = this.handleCardSecureNumberChange.bind(this);
  }

  handleCardNameChange(event) {
    this.setState({cardNumber: event.target.value});
  }

  handleCardExpireChange(event) {
    this.setState({cardExpires: event.target.value});
  }

  handleCardSecureNumberChange(event) {
    this.setState({cardSecureNumber: event.target.value});
  }

  goToConfirmScreen = () => {
    if (!this.state.cardExpires || !this.state.cardSecureNumber || !this.state.cardNumber) {
      <Alert variant={'danger'}>
        Please fill all blank fields!
      </Alert>
      return
    }
    this.props.history.push('/confirm', {
      duration: this.props.location.state.duration, 
      amount: this.props.location.state.amount,
      isUpfront: this.props.location.state.isUpfront,
      priceUsdPerGb: this.props.location.state.priceUsdPerGb
    })
  }

  goToBack = () => {
    this.props.history.goBack();
  } 

  render() {
    return (
      <div className="App">
        <div style={{flexDirection: 'column', display: 'flex', alignItems: 'center', height: '100px', justifyContent: 'space-between'}}>
            <label>
              Credit Card Number:
              <input type="text" value={this.state.cardNumber} onChange={this.handleCardNameChange} />
            </label>
            <label>
              Credit Expire Date:
              <input type="text" value={this.state.cardExpires} onChange={this.handleCardExpireChange} />
            </label>
            <label>
              Credit Card Number:
              <input type="text" value={this.state.cardSecureNumber} onChange={this.handleCardSecureNumberChange} />
            </label>
        </div>
        <div style={{justifyContent: 'space-between', display: 'flex', marginRight: "2rem", marginTop: "2rem"}}>
          <div style={{flexDirection: 'column', display: "flex", justifyContent: "center", borderRadius: '5px', borderColor: 'black', backgroundColor: 'gainsboro', padding: '1rem'}}>
            <div><span style={{fontWeight: "bold", color: "black"}}>Current Subscription:</span> {this.props.location.state.duration} Months Subscription</div>
            <div><span style={{fontWeight: "bold", color: "black"}}>Total Amount: </span>{this.props.location.state.amount}$</div>
          </div>
          <div style={{display: "flex", flexDirection: "row"}}>
            <div onClick={this.goToBack} style={{cursor: 'pointer', width: "150px", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "gray", marginRight: "2rem"}}>
              <span style={{fontWeight: "bold", color: "white"}}>Back</span>
            </div>
            <div onClick={this.goToConfirmScreen} style={{cursor: 'pointer', width: "150px", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "green"}}>
              <span style={{fontWeight: "bold", color: "white"}}>Next</span>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(PaymentPage);
