import React from 'react';
import { withRouter } from 'react-router-dom';
import Breadcrumb from 'react-bootstrap/Breadcrumb';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
class PaymentPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cardNumber: '',
      cardExpire: '',
      cardSecureNumber: '',
    };

    this.handleCardNumberChange = this.handleCardNumberChange.bind(this);
    this.handleCardExpireChange = this.handleCardExpireChange.bind(this);
    this.handleCardSecureNumberChange = this.handleCardSecureNumberChange.bind(this);
  }

  handleCardNumberChange(event) {
    if (event.target.value.length <= 16) {
      this.setState({cardNumber: event.target.value});
      return
    }
  }

  handleCardExpireChange(event) {
    if (event.target.value.length <= 5) {
      let date = event.target.value;
      if (date[0] !== '1' && date[0] !== '0') {
        date = '';
      }
      if (date.length === 2) {
        if (parseInt(date.substring(0, 2)) > 12 || parseInt(date.substring(0, 2)) == 0) {
          date = date[0];
        } else if (this.state.cardExpire.length == 1) {
          date += '/';
        } else {
          date = date[0];
        }
      }
      this.setState({cardExpire: date});
    }
  }

  handleCardSecureNumberChange(event) {
    if (event.target.value.length <= 3) {
      this.setState({cardSecureNumber: event.target.value});
      return
    }
  }

  goToConfirmScreen = () => {
    if (!this.state.cardExpire || this.state.cardSecureNumber.length != 3 || this.state.cardNumber.length != 16) {
      toast("Please fill all blank fields correctly!", {
        className: 'toast-error',
      })
      return
    }
    this.props.history.push('/confirm', {
      duration: this.props.location.state.duration, 
      amount: this.props.location.state.amount,
      isUpfront: this.props.location.state.isUpfront,
      priceUsdPerGb: this.props.location.state.priceUsdPerGb,
      cardNumber: this.state.cardNumber,
      cardExpire: this.state.cardExpire,
      cvc: this.state.cardSecureNumber
    })
  }

  goToBack = () => {
    this.props.history.goBack();
  } 

  render() {
    return (
      <div className="App">
      <Breadcrumb>
        <Breadcrumb.Item onClick={() => this.props.history.push('/')}>Subscription Options</Breadcrumb.Item>
        <Breadcrumb.Item active>Payment</Breadcrumb.Item>
      </Breadcrumb>
        <div style={{flexDirection: 'column', display: 'flex', alignItems: 'center', justifyContent: 'space-between'}}>
        <div style={{width: '60%', display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexDirection: 'row'}}>
            <div style={{width: '50%'}}>
              <Form.Group>
                <Form.Label>Card Number</Form.Label>
                <Form.Control value={this.state.cardNumber} onChange={this.handleCardNumberChange} type="number" placeholder="Enter card number" />
              </Form.Group>
            </div>

            <div style={{width: '15%'}}>
              <Form.Group>
                <Form.Label>Expires Date</Form.Label>
                <Form.Control value={this.state.cardExpire} onChange={this.handleCardExpireChange} type="text" placeholder="MM/DD" />
              </Form.Group>
            </div>

            <div style={{width: '17%'}}>
              <Form.Group>
                <Form.Label>CVC</Form.Label>
                <Form.Control value={this.state.cardSecureNumber} onChange={this.handleCardSecureNumberChange} type="number" placeholder="cvc" />
              </Form.Group>
            </div>
        </div>
        </div>
        <div style={{justifyContent: 'space-between', display: 'flex', marginRight: "2rem", marginTop: "2rem", marginLeft: '2rem'}}>
          <div style={{flexDirection: 'column', display: "flex", justifyContent: "center", borderRadius: '5px', borderColor: 'black', backgroundColor: 'gainsboro', padding: '1rem'}}>
            <div>
              <span style={{fontWeight: "bold", color: "black"}}>Current Subscription:</span> {this.props.location.state.duration} Months Subscription
            </div>
            <div style={{flexDirection: 'row', display: 'flex',}}>
              <span style={{fontWeight: "bold", color: "black"}}>Total Amount: </span>
              {
                this.props.location.state.isUpfront == 0 ? 
                <div><span style={{color: "black"}}>{this.props.location.state.amount}$</span></div> :
                <div>
                  <span style={{color: "black", textDecorationLine: 'line-through', textDecorationStyle: 'solid'}}>{this.props.location.state.amount}$</span> 
                  <span style={{fontWeight: "bold", color: "black", fontSize: '1.1rem', marginLeft: '0.4rem'}}>{this.props.location.state.amount * 0.9}$</span>
                </div>
              }
            </div>
          </div>
          <div style={{display: "flex", flexDirection: "row"}}>
            <div style={{marginRight: '1rem'}}>
              <Button onClick={this.goToBack} variant="secondary"><span style={{fontWeight: "bold", color: "white"}}>Back</span></Button>
            </div>
            <div>
              <Button onClick={this.goToConfirmScreen} variant="success"><span style={{fontWeight: "bold", color: "white"}}>Next</span></Button>
            </div>
          </div>
        </div>
        <ToastContainer/>
      </div>
    );
  }
}

export default withRouter(PaymentPage);
