import React from 'react';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cardNumber: '',
      cardExpires: '',
      cardSecureNumber: '',
    };

    this.handleCardNameChange = this.handleCardNameChange.bind(this);
    this.handleCardExpireChange = this.handleCardExpireChange.bind(this);
    this.handleCardSecureNumberChange = this.handleCardSecureNumberChange.bind(this);
    this.handleSubmitCardForm = this.handleSubmitCardForm.bind(this);
  }

  handleCardNameChange(event) {
    this.setState({cardNumber: event.target.value});
  }

  handleCardExpireChange(event) {
    this.setState({carcardExpiresdE: event.target.value});
  }

  handleCardSecureNumberChange(event) {
    this.setState({cardSecureNumber: event.target.value});
  }

  handleSubmitCardForm(event) {
    alert(this.state.cardNumber + this.state.cardExpires + this.state.cardSecureNumber);
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">
        <form onSubmit={this.handleSubmitCardForm}>
        <label>
          Credit Card Number:
          <input type="text" value={this.state.cardNumber} onChange={this.handleCardNameChange} />
        </label>
        <label>
          Credit Expire Date:
          <input type="text" value={this.state.cardNumber} onChange={this.handleCardNameChange} />
        </label>
        <label>
          Credit Card Number:
          <input type="text" value={this.state.cardNumber} onChange={this.handleCardNameChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      </div>
    );
  }
}
