import React from 'react';
import { withRouter } from 'react-router-dom';

class SubscriptionPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedDuration: 12,
      priceUsdPerGb: 2,
      fetchData: [],
      selectedStorage: 5,
      selectedUpFrontPayment: 0,
      totalAmount: null,
    }
    this.onDurationValueChange = this.onDurationValueChange.bind(this);
    this.onStorageValueChange = this.onStorageValueChange.bind(this);
    this.onUpfrontValueChange = this.onUpfrontValueChange.bind(this);
  }

  componentDidMount() {
    const requestOptions = {
      method: 'GET',
    };
    fetch('https://cloud-storage-prices-moberries.herokuapp.com/prices', requestOptions)
        .then(response => response.json())
        .then(data => this.setState({fetchData: data.subscription_plans}));
  }

  onDurationValueChange = event => {
      this.setState({
        selectedDuration: event.target.value,
      });
      for (let index = 0; index < this.state.fetchData.length; index++) {
        if (this.state.fetchData[index].duration_months == event.target.value) {
          this.setState({priceUsdPerGb: this.state.fetchData[index].price_usd_per_gb}, () => {
            this.setState({totalAmount: this.state.selectedDuration*this.state.priceUsdPerGb*this.state.selectedStorage})
          })
          return
        }
      }
  }

  onStorageValueChange = event => {
    this.setState({
      selectedStorage: event.target.value,
    }, () => {
      this.setState({totalAmount: this.state.selectedDuration*this.state.priceUsdPerGb*this.state.selectedStorage})
    });
  }

  onUpfrontValueChange = event => {
    this.setState({
      selectedUpFrontPayment: event.target.value
    });
  }

  goToPaymentScreen = () => {
    this.props.history.push('/payment', {duration: this.state.selectedDuration, amount: this.state.totalAmount})
  } 

  render() {
    return (
      <div className="App">
        <h3 style={{textAlign: 'center', color: 'red'}}>Choose Your Duration Time</h3>
        <div style={{flexDirection: 'row', display: 'flex', justifyContent: 'center'}}>
          <input type="radio" onChange={this.onDurationValueChange} value={3} checked={this.state.selectedDuration == 3} /> 3 Months
          <input type="radio" onChange={this.onDurationValueChange} value={6} checked={this.state.selectedDuration == 6} /> 6 Months
          <input type="radio" onChange={this.onDurationValueChange} value={12} checked={this.state.selectedDuration == 12} /> 12 Months
        </div>
        <hr/>
        <h3 style={{textAlign: 'center', color: 'red'}}>Choose Your Amount of Storage in Cloud</h3>
        <div style={{flexDirection: 'row', display: 'flex', justifyContent: 'center'}}>
          <input type="radio" onChange={this.onStorageValueChange} value={5} checked={this.state.selectedStorage == 5} /> 5 GB
          <input type="radio" onChange={this.onStorageValueChange} value={10} checked={this.state.selectedStorage == 10} /> 10 GB
          <input type="radio" onChange={this.onStorageValueChange} value={50} checked={this.state.selectedStorage == 50} /> 50 GB
        </div>
        <hr/>
        <h3 style={{textAlign: 'center', color: 'red'}}>Choose Will You Pay Upfront</h3>
        <div style={{flexDirection: 'row', display: 'flex', justifyContent: 'center'}}>
          <input type="radio" onChange={this.onUpfrontValueChange} value={1} checked={this.state.selectedUpFrontPayment == 1} /> Yes
          <input type="radio" onChange={this.onUpfrontValueChange} value={0} checked={this.state.selectedUpFrontPayment == 0} /> No
        </div>
        <hr/>
        <div style={{justifyContent: 'space-between', display: 'flex', marginRight: "2rem", marginTop: "2rem"}}>
          <div style={{flexDirection: 'column', display: "flex", justifyContent: "center", borderRadius: '5px', borderColor: 'black', backgroundColor: 'gainsboro', padding: '1rem'}}>
            <div><span style={{fontWeight: "bold", color: "black"}}>Current Subscription:</span> {this.state.selectedDuration} Months Subscription</div>
            <div><span style={{fontWeight: "bold", color: "black"}}>Total Amount: </span>{this.state.totalAmount}$</div>
          </div>
          <div onClick={this.goToPaymentScreen} style={{cursor: 'pointer', width: "8%", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "gray"}}>
            <span style={{fontWeight: "bold", color: "white"}}>Next</span>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(SubscriptionPage);
