import React from 'react'

export default class ConfirmationPage extends React.Component {
    confirm = () => {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ title: 'React POST Request Example' })
        };
        fetch('https://httpbin.org/post', requestOptions)
            .then(response => response.json())
            .then(data => console.log(data));
    }
    render() {
        return (
            <div style={{alignItems: 'center', display: 'flex', flexDirection: 'column'}}>
                <div style={{flexDirection: 'column', width: '80%', height: '100px', display: "flex", justifyContent: "center", borderRadius: '5px', borderColor: 'black', backgroundColor: 'gainsboro', padding: '1rem'}}>
                    <div><span style={{fontWeight: "bold", color: "black"}}>Current Subscription:</span>6 Months Subscription</div>
                    <div><span style={{fontWeight: "bold", color: "black"}}>Total Amount: </span>5$</div>
                </div>
                <div style={{display: "flex", flexDirection: "row", marginTop: '2rem'}}>
                    <div onClick={this.goToBack} style={{cursor: 'pointer', width: "150px", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "gray", marginRight: "2rem"}}>
                        <span style={{fontWeight: "bold", color: "white"}}>Back</span>
                    </div>
                    <div onClick={this.goToConfirmScreen} style={{cursor: 'pointer', width: "150px", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "green"}}>
                        <span style={{fontWeight: "bold", color: "white"}}>Confirm</span>
                    </div>
                </div>
            </div>
        )
    }
}
