import React from 'react';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedDuration: '12',
      selectedStorage: '5',
      selectedUpFrontPayment: "0"
    }
    this.onDurationValueChange = this.onDurationValueChange.bind(this);
    this.onStorageValueChange = this.onStorageValueChange.bind(this);
    this.onUpfrontValueChange = this.onUpfrontValueChange.bind(this);
  }

  onDurationValueChange(event) {
      this.setState({
        selectedDuration: event.target.value
      });
  }

  onStorageValueChange(event) {
    this.setState({
      selectedStorage: event.target.value
    });
  }

  onUpfrontValueChange(event) {
    this.setState({
      selectedUpFrontPayment: event.target.value
    });
  }

  render() {
    return (
      <div className="App">
        <h3 style={{textAlign: 'center', color: 'red'}}>Select Your Duration Time</h3>
        <div style={{flexDirection: 'row', display: 'flex', justifyContent: 'center'}}>
          <input type="radio" onChange={this.onDurationValueChange} value="3" checked={this.state.selectedDuration === '3'} /> 3
          <input type="radio" onChange={this.onDurationValueChange} value="6" checked={this.state.selectedDuration === '6'} /> 6
          <input type="radio" onChange={this.onDurationValueChange} value="12" checked={this.state.selectedDuration === '12'} /> 12
        </div>
        <hr/>
        <h3 style={{textAlign: 'center', color: 'red'}}>Select Your Amount of Storage in Cloud</h3>
        <div style={{flexDirection: 'row', display: 'flex', justifyContent: 'center'}}>
          <input type="radio" onChange={this.onStorageValueChange} value="5" checked={this.state.selectedStorage === '5'} /> 5
          <input type="radio" onChange={this.onStorageValueChange} value="10" checked={this.state.selectedStorage === '10'} /> 10
          <input type="radio" onChange={this.onStorageValueChange} value="50" checked={this.state.selectedStorage === '50'} /> 50
        </div>
        <hr/>
        <h3 style={{textAlign: 'center', color: 'red'}}>Select Your Amount of Storage in Cloud</h3>
        <div style={{flexDirection: 'row', display: 'flex', justifyContent: 'center'}}>
          <input type="radio" onChange={this.onUpfrontValueChange} value={"1"} checked={this.state.selectedUpFrontPayment === "1"} /> Yes
          <input type="radio" onChange={this.onUpfrontValueChange} value={"0"} checked={this.state.selectedUpFrontPayment === "0"} /> No
        </div>
        <hr/>
        <div>
          <div style={{width: "8%", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "gray"}}>
            <span style={{fontWeight: "bold", color: "white"}}>Next</span>
          </div>
        </div>
      </div>
    );
  }
}
