import React from 'react';
import { withRouter } from 'react-router-dom';
import Breadcrumb from 'react-bootstrap/Breadcrumb';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import validator from 'validator';
import { ToastContainer, toast } from 'react-toastify';
import Spinner from 'react-bootstrap/Spinner'

class ConfirmationPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            checkbox: false,
            loading: false,
        }

        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handleChangeCheckbox = this.handleChangeCheckbox.bind(this);
    }

    confirm = () => {
        this.setState({loading: true})
        let requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                cardNumber: this.props.location.state.cardNumber,
                cardExpire: this.props.location.state.cardExpire,
                cvc: this.props.location.state.cvc,
                amount: this.props.location.state.amount
            })
        };
        if (this.state.checkbox === true && this.state.email !== '') {
            if (!validator.isEmail(this.state.email)) {
                toast("Enter valid e-mail!", {
                    autoClose: 3000
                });
                this.setState({loading: false})
                return;
            }
            fetch('https://httpbin.org/post', requestOptions)
                .then(response => response.json())
                .then(data => {
                    console.log(data);
                    this.setState({loading: false});
                    this.props.history.push('/success');
                })
                .catch(e => {
                    console.log(e);
                    this.setState({loading: false});
                    toast("Something went wrong, please try again!", {
                        autoClose: 3000
                    });
                })
        } else {
            toast("Please fill all blank fields correctly!", {
                autoClose: 3000
            });
            this.setState({loading: false})
        }
    }

    goToBack = () => {
        this.props.history.goBack();
    }

    handleEmailChange = event => {
        this.setState({email: event.target.value});
    }

    handleChangeCheckbox = () => {
        this.setState({checkbox: !this.state.checkbox});
    }

    render() {
        return (
            <div className="App">
                {this.state.loading ? 
                <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center', width: '100%', height: '500px'}}>
                    <Spinner animation="grow" />
                </div> 
                : 
                <div>
                    <Breadcrumb>
                        <Breadcrumb.Item onClick={() => this.props.history.push('/')}>Subscription Options</Breadcrumb.Item>
                        <Breadcrumb.Item onClick={() => this.props.history.push('/payment', {
                            duration: this.props.location.state.duration, 
                            amount: this.props.location.state.amount,
                            isUpfront: this.props.location.state.isUpfront,
                            priceUsdPerGb: this.props.location.state.priceUsdPerGb
                        })}>Payment</Breadcrumb.Item>
                        <Breadcrumb.Item active>Confirm</Breadcrumb.Item>
                    </Breadcrumb>
                    <div style={{alignItems: 'center', display: 'flex', flexDirection: 'column'}}>
                        <div style={{flexDirection: 'column', width: '80%', height: '220px', display: "flex", justifyContent: "space-evenly", borderRadius: '5px', borderColor: 'black', backgroundColor: 'gainsboro', padding: '1rem'}}>
                            <div><span style={{fontWeight: "bold", color: "black"}}>Selected Subscription: </span>6 Months Subscription</div>
                            <div><span style={{fontWeight: "bold", color: "black"}}>USD Per GB: </span>{this.props.location.state.priceUsdPerGb}$</div>
                            <div style={{flexDirection: 'row', display: 'flex',}}>
                                <span style={{fontWeight: "bold", color: "black"}}>Total Amount: </span>
                                {
                                    this.props.location.state.isUpfront == 0 ? 
                                    <div><span style={{color: "black"}}>{this.props.location.state.amount}$</span></div> :
                                    <div>
                                    <span style={{color: "black", textDecorationLine: 'line-through', textDecorationStyle: 'solid'}}>{this.props.location.state.amount}$</span> 
                                    <span style={{fontWeight: "bold", color: "black", fontSize: '1.1rem', marginLeft: '0.4rem'}}>{this.props.location.state.amount * 0.9}$</span>
                                    </div>
                                }
                            </div>
                            <div><span style={{fontWeight: "bold", color: "black"}}>Upfront Payment:</span> {this.props.location.state.isUpfront ? 'Yes' : 'No'} </div>
                            <div style={{width: "25%"}}>
                                <Form.Control value={this.state.email} onChange={this.handleEmailChange} type="text" placeholder="Please enter your e-mail" />
                            </div>
                            <label>
                                <input type="checkbox" defaultChecked={this.state.checkbox} onChange={this.handleChangeCheckbox} />
                                <span style={{marginLeft: '0.4rem'}}>You must sign Terms and Conditions Agreement Contract before checkout</span>
                            </label>
                        </div>
                        <div style={{display: "flex", flexDirection: "row", marginTop: '2rem'}}>
                            <div style={{marginRight: '1rem'}}>
                                <Button onClick={this.goToBack} variant="secondary"><span style={{fontWeight: "bold", color: "white"}}>Back</span></Button>
                            </div>
                            <div>
                                <Button onClick={this.confirm} variant="success"><span style={{fontWeight: "bold", color: "white"}}>Confirm Order</span></Button>
                            </div>
                        </div>
                    </div>
                    <ToastContainer/>
                </div>
                }
            </div>
        )
    }
}

export default withRouter(ConfirmationPage);
