import React from 'react';
import { withRouter } from 'react-router-dom';

class PaymentPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cardNumber: '',
      cardExpires: '',
      cardSecureNumber: '',
    };

    this.handleCardNameChange = this.handleCardNameChange.bind(this);
    this.handleCardExpireChange = this.handleCardExpireChange.bind(this);
    this.handleCardSecureNumberChange = this.handleCardSecureNumberChange.bind(this);
  }

  handleCardNameChange(event) {
    this.setState({cardNumber: event.target.value});
  }

  handleCardExpireChange(event) {
    this.setState({carcardExpiresdE: event.target.value});
  }

  handleCardSecureNumberChange(event) {
    this.setState({cardSecureNumber: event.target.value});
  }

  goToConfirmScreen = () => {
    if (!this.state.cardExpires || !this.state.cardSecureNumber || !this.state.cardNumber) {
      alert('Please fill all blank fields');
      return
    }
    this.props.history.push('confirm')
  }

  goToBack = () => {
    this.props.history.goBack();
  } 

  render() {
    return (
      <div className="PaymentPage">
      <div style={{flexDirection: 'column'}}>
        <form onSubmit={this.handleSubmitCardForm}>
          <label>
            Credit Card Number:
            <input type="text" value={this.state.cardNumber} onChange={this.handleCardNameChange} />
          </label>
          <label>
            Credit Expire Date:
            <input type="text" value={this.state.cardNumber} onChange={this.handleCardNameChange} />
          </label>
          <label>
            Credit Card Number:
            <input type="text" value={this.state.cardNumber} onChange={this.handleCardNameChange} />
          </label>
        </form>
      </div>
        <div style={{justifyContent: 'space-between', display: 'flex', marginRight: "2rem", marginTop: "2rem"}}>
          <div style={{flexDirection: 'column', display: "flex", justifyContent: "center", borderRadius: '5px', borderColor: 'black', backgroundColor: 'gainsboro', padding: '1rem'}}>
            <div><span style={{fontWeight: "bold", color: "black"}}>Current Subscription:</span> {this.props.route.params.duration} Months Subscription</div>
            <div><span style={{fontWeight: "bold", color: "black"}}>Total Amount: </span>{this.props.route.params.amount}$</div>
          </div>
          <div style={{display: "flex", flexDirection: "row"}}>
            <div onClick={this.goToBack} style={{cursor: 'pointer', width: "150px", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "gray", marginRight: "2rem"}}>
              <span style={{fontWeight: "bold", color: "white"}}>Back</span>
            </div>
            <div onClick={this.goToConfirmScreen} style={{cursor: 'pointer', width: "150px", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "gray"}}>
              <span style={{fontWeight: "bold", color: "white"}}>Next</span>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(PaymentPage);
