import React from 'react';
import { withRouter } from 'react-router-dom';

class ConfirmationPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            checkbox: false,
        }

        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handleChangeCheckbox = this.handleChangeCheckbox.bind(this);
    }

    confirm = () => {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ title: 'React POST Request Example' })
        };
        fetch('https://httpbin.org/post', requestOptions)
            .then(response => response.json())
            .then(data => console.log(data));
    }

    goToBack = () => {
        this.props.history.goBack();
    }

    handleEmailChange = event => {
        this.setState({cardSecureNumber: event.target.value});
    }

    handleChangeCheckbox = event => {
        this.setState({checkbox: event.target.value});
    }

    render() {
        return (
            <div style={{alignItems: 'center', display: 'flex', flexDirection: 'column'}}>
                <div style={{flexDirection: 'column', width: '80%', height: '100px', display: "flex", justifyContent: "center", borderRadius: '5px', borderColor: 'black', backgroundColor: 'gainsboro', padding: '1rem'}}>
                    <div><span style={{fontWeight: "bold", color: "black"}}>Current Subscription:</span>6 Months Subscription</div>
                    <div><span style={{fontWeight: "bold", color: "black"}}>USD Per GB: </span>{this.props.location.state.priceUsdPerGb}$</div>
                    <div><span style={{fontWeight: "bold", color: "black"}}>Total Amount: </span>{this.props.location.state.amount}$</div>
                    <label>
                        E-mail Address:
                        <input type="text" value={this.state.email} onChange={this.handleEmailChange} />
                    </label>
                    <label>
                        You must sign Terms and Conditions Agreement Contract before checkout
                        <input type="checkbox" defaultChecked={this.state.checkbox} onChange={this.handleChangeCheckbox} />
                    </label>
                </div>
                <div style={{display: "flex", flexDirection: "row", marginTop: '2rem'}}>
                    <div onClick={this.goToBack} style={{cursor: 'pointer', width: "150px", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "gray", marginRight: "2rem"}}>
                        <span style={{fontWeight: "bold", color: "white"}}>Back</span>
                    </div>
                    <div onClick={this.goToConfirmScreen} style={{cursor: 'pointer', width: "150px", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "green"}}>
                        <span style={{fontWeight: "bold", color: "white"}}>Confirm</span>
                    </div>
                </div>
            </div>
        )
    }
}

export default withRouter(ConfirmationPage);
