import React from 'react';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedDuration: '12',
    }
  }

  onValueChange(event) {
      this.setState({
        selectedDuration: event.target.value
      });
  }

  render() {
    return (
      <div className="App">
        <div style={{flexDirection: 'row'}}>
          <input type="radio" onChange={this.onValueChange} value="3" checked={this.state.selectedDuration === '3'} /> 3
          <input type="radio" onChange={this.onValueChange} value="6" checked={this.state.selectedDuration === '6'} /> 6
          <input type="radio" onChange={this.onValueChange} value="12" checked={this.state.selectedDuration === '12'} /> 12
        </div>
      </div>
    );
  }
}
