import React from 'react';
import { withRouter } from 'react-router-dom';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cardNumber: '',
      cardExpires: '',
      cardSecureNumber: '',
    };

    this.handleCardNameChange = this.handleCardNameChange.bind(this);
    this.handleCardExpireChange = this.handleCardExpireChange.bind(this);
    this.handleCardSecureNumberChange = this.handleCardSecureNumberChange.bind(this);
    this.handleSubmitCardForm = this.handleSubmitCardForm.bind(this);
  }

  handleCardNameChange(event) {
    this.setState({cardNumber: event.target.value});
  }

  handleCardExpireChange(event) {
    this.setState({carcardExpiresdE: event.target.value});
  }

  handleCardSecureNumberChange(event) {
    this.setState({cardSecureNumber: event.target.value});
  }

  handleSubmitCardForm(event) {
    alert(this.state.cardNumber + this.state.cardExpires + this.state.cardSecureNumber);
    event.preventDefault();
  }

  goToConfirmScreen = () =>Â {
    alert('p')
  } 

  render() {
    return (
      <div className="App">
      <div style={{flexDirection: 'column'}}>
        <form onSubmit={this.handleSubmitCardForm}>
          <label>
            Credit Card Number:
            <input type="text" value={this.state.cardNumber} onChange={this.handleCardNameChange} />
          </label>
          <label>
            Credit Expire Date:
            <input type="text" value={this.state.cardNumber} onChange={this.handleCardNameChange} />
          </label>
          <label>
            Credit Card Number:
            <input type="text" value={this.state.cardNumber} onChange={this.handleCardNameChange} />
          </label>
          <input type="submit" value="Submit" />
        </form>
      </div>
        <div style={{justifyContent: 'flex-end', display: 'flex', marginRight: "2rem", marginTop: "2rem"}}>
          <div onClick={this.goToConfirmScreen} style={{cursor: 'pointer', width: "8%", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "gray", marginRight: "2rem"}}>
            <span style={{fontWeight: "bold", color: "white"}}>Back</span>
          </div>
          <div onClick={this.goToConfirmScreen} style={{cursor: 'pointer', width: "8%", height: "2rem", display: "flex", justifyContent: "center", alignItems: "center", backgroundColor: "gray"}}>
            <span style={{fontWeight: "bold", color: "white"}}>Next</span>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(App);
