import React from 'react';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cardNumber: '',
      cardExpires: '',
      cardSecureNumber: '',
    };

    this.handleCardNameChange = this.handleCardNameChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleCardNameChange(event) {
    this.setState({cardNumber: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.cardNumber);
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">
        <form onSubmit={this.handleSubmit}>
        <label>
          Credit Card Number:
          <input type="text" value={this.state.cardNumber} onChange={this.handleCardNameChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      </div>
    );
  }
}
